import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import SingleMenu from './SingleMenu';


function Home() {
  const [Menus, setMenus] = useState([]);
  const [Reviews, setReviews] = useState([]);

  useEffect(() => {
    fetchMenus('http://127.0.0.1:8000/api/menus/');
    fetchReview('http://127.0.0.1:8000/api/rating/');
  }, []);

  const fetchReview = (baseurl) => {
    axios.get(baseurl)
      .then(response => {
        setReviews(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  const fetchMenus = (baseurl) => {
    axios.get(baseurl)
      .then(response => {
        setMenus(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  const showStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <i key={i} className={`fa-solid fa-star ${i <= rating ? 'text-warning' : ''}`}></i>
      );
    }
    return stars;
  };

  return (
    <div className="container">
      <main class="container my-5 border border-2 mx-auto p-4">
        <div class="row">
          <div class="col-md-6 text-center my-auto">
            <h3 class=" font-weight-bold mb-4">Welcome to Our Restaurant</h3>
            <p class="lead">Experience the best dining with our exquisite menu and cozy atmosphere.</p>
            <a href="/" class="btn btn-primary mt-4">Get Start</a>
          </div>
          <div class="col-md-6">
            <img class="w-75 mt-4" src="https://img.freepik.com/premium-vector/chef-cooking-outdoor-barbecue_24640-71290.jpg?w=740" alt="Restaurant"/>
          </div>
        </div>
      </main>


      <div className='d-flex align-items-center justify-content-between'>
        <h3 className='my-4 mb-5'>All Menus</h3>
        <Link to="/showAllMenu" className='mb-4 btn btn-dark text-md-end'>View All Menus <i className="fa-solid fa-arrow-right px-1"></i></Link>
      </div>
      <div className='row'>
        {Menus.map((menu, index) => (
          <SingleMenu key={index} menu={menu} />
        ))}
      </div>

      {/* Reviews and rating start */}
      <div id="carouselExampleIndicators" className="carousel slide my-4" data-bs-ride="carousel">
        <div className="carousel-indicators">
          {Reviews.map((_, index) => (
            <button
              type="button"
              key={index}
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide-to={index}
              className={index === 0 ? 'active' : ''}
              aria-current={index === 0 ? 'true' : 'false'}
              aria-label={`Slide ${index + 1}`}
            ></button>
          ))}
        </div>
        <div className="carousel-inner bg-dark">
          {Reviews.length === 0 ? (
            <div className="carousel-item active p-5">
              <p className="text-white text-center">No Review Found</p>
            </div>
          ) : (
            Reviews.map((item, index) => (
              <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''} p-5`}>
                <figure className="text-center">
                  <blockquote className="blockquote">
                    <p className='text-white'>{item.comment}</p>
                  </blockquote>
                  <figcaption className="blockquote-footer warning">
                    {showStars(item.rating)}
                    <cite title="Source Title">{item.user}</cite>
                  </figcaption>
                </figure>
              </div>
            ))
          )}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
      {/* Reviews and rating end */}


      <h2 class="px-5 mt-5 mx-auto text-center font-italic display-4">Blogs
    <div class="hr-line"></div>
</h2>

<div class="d-flex gap-3 justify-content-center align-items-center flex-wrap">
<div className="card" style={{ width: '25rem' }}>
        <img class="card-img-top p-3" src="data:image/jpeg;base64," alt="Card image cap"/>
        <div class="card-body">
            <h5 class="card-title">DelhiDarbar</h5>
            <p class="card-text">Largest food restaurant in Bangladesh. Elements offers the most variety of food choices you can find anywhere in Dhaka. More than 130 items.</p>
        </div>
    </div>
    <div className="card" style={{ width: '25rem' }}>
        <img class="card-img-top p-3" src="data:image/jpeg;base64," alt="Card image cap"/>
        <div class="card-body">
            <h5 class="card-title">Sagar Ratna</h5>
            <p class="card-text">Indians have a long history of being known for their love of cuisine. Although the concept of restaurants is not entirely new in our country. </p>
        </div>
    </div>
    <div className="card" style={{ width: '25rem' }}>
        <img class="card-img-top p-3" src="data:image/jpeg;base64," alt="Card image cap"/>
        <div class="card-body">
            <h5 class="card-title">Caf√© Coffee Day</h5>
            <p class="card-text">Subway is one of the most recognizable and well-known food changes the flavors and Aroma of freshly made bread with a variety of topping.izable and well-known food changes the flavors and Aroma of freshly made bread with .</p>
        </div>
    </div>
</div>





    </div>
  );
}

export default Home;
